if myHero.charName ~= "Garen" then return end
local direction = 0 local spin = false function OnTick() for _,enemy in pairs(GetEnemyHeroes()) do if ValidTarget(enemy, 450) and myHero:CalcMagicDamage(enemy,175*myHero:GetSpellData(_R).level+(enemy.maxHealth-enemy.health)/(4-myHero:GetSpellData(_R).level*0.5)) > enemy.health then CastSpell(_R, enemy) DelayAction(function() if enemy.dead and EnemiesAround(myHero,750) == 0 then spin = true end end, 1) end end if spin then DelayAction(function() spin = false end, 3) myHero:MoveTo(myHero.x + 100*math.sin(math.pi*direction / 10),myHero.z + 100*math.cos(math.pi*direction / 10)) direction = direction + 1 end end function EnemiesAround(Unit, range) local c=0 if Unit == nil then return 0 end for i=1,heroManager.iCount do hero = heroManager:GetHero(i) if hero ~= nil and hero.team ~= myHero.team and hero.x and hero.y and hero.z and GetDistance(hero, Unit) < range then c=c+1 end end return c end
print("Ultimate Garen Script Activated.")